mapper.insert:
insert
  into data_board
  (
     DB_NO
   , DB_WRITER
   , DB_TITLE
   , DB_CONTENT
   , ORIGIN_NAME
   , CHANGE_NAME
  )
  values
  (
     seq_dbno.nextval
   , #{boardWriter}
   , #{boardTitle}
   , #{boardContent}
   , #{originName}
   , #{changeName}
  )

mapper.update:
update
	   data_board
   set DB_TITLE = #{boardTitle}
     , DB_CONTENT = #{boardContent}
	 , ORIGIN_NAME = #{originName}
	 , CHANGE_NAME = #{changeName}
 where db_no = #{boardNo}
